import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-add-task',
  templateUrl: './add-task.component.html',
  styleUrls: ['./add-task.component.scss'],
})
export class AddTaskComponent {
  public taskInput: string;
  public taskList: any[] = [];

  @Output() addTaskEmitter = new EventEmitter();

  public onAddTask() {
    const index = this.taskList.findIndex((task) => task === this.taskInput);
    if (index === -1) {
      this.taskList.push({ taskLabel: this.taskInput, isComplete: false });
      this.taskInput = null;
      this.addTaskEmitter.emit(this.taskList);
    }
  }

  public getInputVal(val: string) {
    this.taskInput = val;
  }
}
<div class="d-flex align-items-end add-task p-4">
  <div class="w-75">
    <label for="task" class="fw-bold me-3 mb-2">Task</label>
    <input
      id="task"
      type="text"
      class="form-control w-100"
      placeholder="Type Task"
      [(ngModel)]="taskInput"
    />
  </div>
  <button class="btn btn-primary ms-3 fw-bold w-25" (click)="onAddTask()">
    Add Task
  </button>
</div>
