import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.scss'],
})
export class ListComponent {
  @Input() tasks: string[];
  @Output() deleteTaskEmitter = new EventEmitter();

  public deleteTask(i: number) {
    this.tasks.splice(i, 1);
    this.deleteTaskEmitter.emit(this.tasks);
  }
}
<div class="mt-5 p-4">
  <ul class="list-unstyled w-50">
    <li
      *ngFor="let task of tasks; let i = index"
      class="p-3 bg-secondary bg-gradient d-flex justify-content-between my-2"
    >
      <span>{{ task }}</span>
      <div>
        <button class="btn bg-danger text-white me-2" (click)="deleteTask(i)">
          Delete
        </button>
        <button class="btn btn-success text-white">Complete</button>
      </div>
    </li>
  </ul>
</div>
