import { Component } from '@angular/core';

@Component({
  selector: 'app-add-task',
  templateUrl: './add-task.component.html',
  styleUrls: ['./add-task.component.scss'],
})
export class AddTaskComponent {
  public taskInput: string;
  public taskList: string[] = [];

  public addTask() {
    const index = this.taskList.findIndex((task) => task === this.taskInput);
    console.log(index);

    if (index === -1) {
      this.taskList.push(this.taskInput);
      this.taskInput = null;
    }
    console.log(this.taskList);
  }
}
<div class="d-flex align-items-center">
  <div>
    <label for="" class="fw-bold me-3">Task</label>
    <input
      type="text"
      class="form-control"
      placeholder="Type Task"
      [(ngModel)]="taskInput"
    />
  </div>
  <button class="btn btn-primary ms-3 fw-bold w-25" (click)="addTask()">
    Add Task
  </button>
</div>
