import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  public taskList: any[];
  public result: string;

  public getTaskList(tasks: any[]) {
    this.taskList = tasks;
  }

  public updateTaskList(updatedTasks: any[]) {
    this.taskList = updatedTasks;
  }

  public completeTask(index: number) {
    this.taskList[index].isComplete = !this.taskList[index].isComplete;
  }
}
<div class="container p-5 mt-5 mx-auto">
  <h2 class="text-info text-uppercase fw-bold mb-5">Task Application</h2>

  <app-add-task (addTaskEmitter)="getTaskList($event)"></app-add-task>
  <app-list
    [tasks]="taskList"
    (deleteTaskEmitter)="updateTaskList($event)"
    (completeTaskEmitter)="completeTask($event)"
  ></app-list>
</div>

<input type="text" #inputVal />
<p>{{ inputVal.value }}</p>

