import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
})
export class AppComponent {
  public taskList: any[];

  public getTaskList(tasks: string[]) {
    this.taskList = tasks;
    this.checkTaskStatus();
  }

  public checkTaskStatus() {
    this.taskList = this.taskList.map((task) => {
      task.isComplete = false;
      return task;
    });
  }

  public updateTaskList(updatedTasks: any[]) {
    this.taskList = updatedTasks;
  }

  public completeTask(index: number) {
    this.taskList[index].isComplete = true;
  }
}
<div class="container p-5">
  <h2 class="text-info text-uppercase fw-bold mb-5">Task Application</h2>
  <app-add-task (addTaskEmitter)="getTaskList($event)"></app-add-task>
  <app-list
    [tasks]="taskList"
    (deleteTaskEmitter)="updateTaskList($event)"
    (completeTaskEmitter)="completeTask($event)"
  ></app-list>
</div>
